
public class Day5 {
	
	/**
	 * Solution for Advent of Code 2018 - Day 5 pt 1
	 * 
	 * @param input - String denoting polymer
	 * @return 		- int denoting length of polymer after reaction
	 */
	public static int solution1(String input) {
		
		//TODO Refactor to be more efficient, takes an extremely long time
		boolean differingCases = false;
		StringBuilder builder = new StringBuilder();
		builder.append(input);
		boolean moreToDestroy = true;
		
		while (moreToDestroy) {
			moreToDestroy = false;
			for (int i = 0; i < builder.toString().length() - 1; i++) {
				
				differingCases = false;
				char firstChar = builder.charAt(i);
				char secondChar = builder.charAt(i+1);
				
				if ((Character.isUpperCase(firstChar) && Character.isLowerCase(secondChar)) ||
						(Character.isUpperCase(secondChar) && Character.isLowerCase(firstChar))) {
					differingCases = true;
				}
				
				// destroy two chars if same char but differing cases
				if (Character.toLowerCase(firstChar) == Character.toLowerCase(secondChar) && differingCases) {
					builder.replace(i, i+2, "");
					moreToDestroy = true;
				}
				
			}
		}
		
		return builder.toString().length();
		
	}
	
	/**
	 * Solution for Advent of Code 2018 - Day 5 pt 2
	 * 
	 * @param input - String denoting polymer
	 * @return 		- int denoting polymer length after removing most problematic character
	 */
	public static int solution2(String input) {
		
		int minimumPolymerLength = Integer.MAX_VALUE;
		// determine minimum polymer length from removing a single character
		for (char ch = 'a'; ch <= 'z'; ch++) {
			String character = Character.toString(ch).toLowerCase();
			System.out.println("Removing character: " + character + " and " + character.toUpperCase());
			String newInput = input.replaceAll(character, "");
			newInput = newInput.replaceAll(character.toUpperCase(), "");
			
			
			int lengthAfterReduction = 0;
			lengthAfterReduction = solution1(newInput);
			System.out.println(lengthAfterReduction);
			if (lengthAfterReduction < minimumPolymerLength) {
				minimumPolymerLength = lengthAfterReduction;
			}
			
		}
		
		return minimumPolymerLength;
			
	}

	public static void main(String[] args) {
		
		// test input
		String input = "";
		
		// exercise solution given test input
		long startTime = System.currentTimeMillis();
		int soln = solution2(input);
		// int soln = solution2(input);
		long endTime = System.currentTimeMillis();

		// print solution and runtime
		long totalTime = endTime - startTime;
		System.out.println("Solution Output: " + soln);
		System.out.println("Solution Runtime: " + totalTime + " ms");
	}
}
